mapper.select:
select

from



        and a.id = #{id,jdbcType=INTEGER}


        and a.menu_name = #{menuName,jdbcType=VARCHAR}


        and a.menu_url = #{menuUrl,jdbcType=VARCHAR}


        and a.parent_id = #{parentId,jdbcType=INTEGER}


        and a.permission_name = #{permissionName,jdbcType=INTEGER}


        and a.is_show = #{isShow,jdbcType=CHAR}


        and a.sort = #{sort,jdbcType=INTEGER}

mapper.update:
update sys_menu
menu_name = #{menuName,jdbcType=VARCHAR}, menu_url = #{menuUrl,jdbcType=VARCHAR}, parent_id = #{parentId,jdbcType=INTEGER}, permission_name = #{permissionName,jdbcType=INTEGER}, is_show = #{isShow,jdbcType=CHAR}, sort = #{sort,jdbcType=INTEGER},
and id = #{id,jdbcType=INTEGER}
