.header {
  height: 62px;
  background-color: #fff;
  font-size: 16px;
}
.header .header_container {
  background-color: #fff;
  border-bottom: 1px solid #ddd;
  border-top: none;
  padding: 0 16px;
  position: fixed;
  max-width: 1920px;
  margin: 0 auto;
  top: 0px;
  left: 0px;
  right: 0px;
  height: 60px;
  z-index: 2;
}
.header .header_container .header_left .logo {
  line-height: 60px;
  height: 60px;
  font-size: 25px;
  cursor: pointer;
  color: #ea6f5a;
  font-weight: bold;
}
.header .header_container .header_left .header_conterl {
  display: inline-block;
  padding: 18px 10px 17px;
  vertical-align: middle;
  float: right;
}
.header .header_container .header_left .header_conterl span {
  display: block;
  width: 20px;
  height: 2px;
  margin-bottom: 5px;
  position: relative;
  background: #ea6f5a;
  border-radius: 3px;
  z-index: 1;
  -webkit-transform-origin: 20px;
  transform-origin: 20px;
  -webkit-transition: background 0.5s cubic-bezier(0.77, 0.2, 0.05, 1), opacity 0.55s ease, width 0.5s, -webkit-transform 0.5s cubic-bezier(0.77, 0.2, 0.05, 1);
  transition: background 0.5s cubic-bezier(0.77, 0.2, 0.05, 1), opacity 0.55s ease, width 0.5s, -webkit-transform 0.5s cubic-bezier(0.77, 0.2, 0.05, 1);
  transition: background 0.5s cubic-bezier(0.77, 0.2, 0.05, 1), opacity 0.55s ease, transform 0.5s cubic-bezier(0.77, 0.2, 0.05, 1), width 0.5s;
  transition: background 0.5s cubic-bezier(0.77, 0.2, 0.05, 1), opacity 0.55s ease, transform 0.5s cubic-bezier(0.77, 0.2, 0.05, 1), width 0.5s, -webkit-transform 0.5s cubic-bezier(0.77, 0.2, 0.05, 1);
}
.header .phone_menu {
  position: fixed;
  left: 0;
  right: 0;
  top: 60px;
  z-index: 1000;
  background-color: #fff;
  padding: 0 10px;
  -webkit-transition: height 0.3s;
  transition: height 0.3s;
  overflow: hidden;
}
.header .phone_menu .active {
  border-bottom: 0.5px solid #ddd;
}
.header .phone_menu .iconfont {
  color: #ea6f5a;
}
@media (max-width: 891px) {
.header .mu-input {
    width: 190px;
}
}
@media (min-width: 768px) {
.header .header_container .header_left .header_conterl {
    display: none;
}
}
